import React, {useEffect} from 'react'
import auth0 from '../lib/auth0'
import router from 'next/router'
import { checkExists, findChecksNearbyCheckin } from '../model/markers'

const App = props =>
{
    //this code snippet "useEffect" just to see is logged
    useEffect(() => {
        if(!props.isAuth)
        {
            router.push('/')
        }else if(props.forceCreate)
        {
            router.push('/create-status')
        }
    })
    if(!props.isAuth || props.forceCreate)
    {
        return null
    }
    return( 
        <div>
            <h1>Status próximo a você:</h1>
            <table>
               {props.checkins.map(checkin => {
                   return( 
                       <tr>
                          <td>{checkin.id === props.user.sub && 'Seu status -'} </td>
                          <td>{checkin.status}</td>
                          <td>{JSON.stringify(checkin.coords)}</td>
                          <td>{checkin.distance}</td>
                       </tr>
                   )
                 })}
            </table>
        </div>
    )
}
export default App  
//get server, use getServerSideProps
//this code below does not go front end, only server
export async function getServerSideProps({req, res}){
    let user = { }
    let isAuth = false
    let forceCreate = false
    let checkinsList = [ ]

    const session = await auth0.getSession(req)
    
    if(session)
    {
        isAuth = true
        user = session.user

        const todaysData = await checkExists(session.user.sub)

        if(!todaysData){
            forceCreate = true
        }else{            
          const checkinsList = await findChecksNearbyCheckin(todaysData)
        }
    }
    return{
        props: {
            isAuth,
            user,
            forceCreate,
            checkinsList
        }
    }
}
