import auth0 from '../../lib/auth0'

const me = async (request, response) => {
    try{
      await auth0.handleProfile(request, response)
    }catch(err){
        response.status(500).end(err.message)
    }
}
export default me
//handleProfile: find out if user is logged in, if logged in returns that user, otherwise an error will be triggered
